document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".product-box").forEach(t=>{t.addEventListener("click",function(t){if(t.target.closest("button")||t.target.closest("a")||t.target.closest("form")||t.target.closest(".add-to-cart-form"))return;const e=this.dataset.productUrl;e&&(window.location.href=e)})})});